import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/survey.css';

const LoginPage = () => {
  const [email, setEmail] = useState('test@example.com');
  const [password, setPassword] = useState('1234');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [backendStatus, setBackendStatus] = useState('checking');
  const navigate = useNavigate();

  // Check backend health on component mount
  useEffect(() => {
    const checkBackend = async () => {
      try {
        await axios.get('http://localhost:5001/api/health', {
          timeout: 3000
        });
        setBackendStatus('healthy');
      } catch (err) {
        setBackendStatus('unavailable');
        setError('Backend service is unavailable. Please try again later.');
        console.error('Backend connection error:', err);
      }
    };
    checkBackend();
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError('Please fill in all fields');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const res = await axios.post(
        'http://localhost:5001/api/login', 
        { email, password },
        { 
          withCredentials: true, // Essential for sessions
          headers: {
            'Content-Type': 'application/json'
          },
          timeout: 10000
        }
      );

      if (res.data.success) {
        // Store minimal user data
        localStorage.setItem('userEmail', email);
        navigate('/home');
      } else {
        setError(res.data.message || 'Login failed. Please check your credentials.');
      }
    } catch (err) {
      let errorMessage = 'Login service unavailable';

      if (err.response) {
        // Server responded with error status
        errorMessage = err.response.data?.message || `Server error (${err.response.status})`;
      } else if (err.request) {
        // Request was made but no response
        errorMessage = 'No response from server - check your connection';
      } else {
        // Other errors
        errorMessage = err.message || 'Login failed';
      }

      setError(errorMessage);
      console.error('Login error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <h2>Survey System Login</h2>

      {backendStatus === 'checking' && (
        <div className="status-message">Connecting to server...</div>
      )}

      {backendStatus === 'unavailable' && (
        <div className="error-message">
          {error}
          <button onClick={() => window.location.reload()} className="retry-btn">
            Retry Connection
          </button>
        </div>
      )}

      {backendStatus === 'healthy' && (
        <>
          {error && <div className="error-message">{error}</div>}

          <form onSubmit={handleLogin}>
            <div className="form-group">
              <label>Email:</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Password:</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                disabled={loading}
              />
            </div>

            <button 
              type="submit" 
              disabled={loading || backendStatus !== 'healthy'}
              className={loading ? 'loading' : ''}
            >
              {loading ? 'Logging in...' : 'Login'}
            </button>
          </form>

          <div className="demo-credentials">
            <p>Demo credentials:</p>
            <p>Email: test@example.com</p>
            <p>Password: 1234</p>
          </div>
        </>
      )}
    </div>
  );
};

export default LoginPage;
