import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function SurveyPage() {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [topic, setTopic] = useState('');
  const navigate = useNavigate();

  // Load questions and topic
  useEffect(() => {
    const loadSurveyData = async () => {
      try {
        // Get topic from localStorage or navigate back if missing
        const currentTopic = localStorage.getItem('currentTopic');
        if (!currentTopic) {
          navigate('/home');
          return;
        }
        setTopic(currentTopic);

        // Get questions from API instead of localStorage
        const res = await axios.get(
          `http://localhost:5001/api/topic-questions/${encodeURIComponent(currentTopic)}`,
          { withCredentials: true }
        );

        if (res.data.success) {
          setQuestions(res.data.questions);
          // Initialize answers with empty strings or default values
          const initialAnswers = {};
          res.data.questions.forEach((q, i) => {
            initialAnswers[i] = '';
          });
          setAnswers(initialAnswers);
        } else {
          throw new Error(res.data.message || 'Failed to load questions');
        }
      } catch (err) {
        setError(err.response?.data?.message || err.message || 'Failed to load survey');
      } finally {
        setLoading(false);
      }
    };

    loadSurveyData();
  }, [navigate]);

  const handleSubmit = async () => {
    setSubmitting(true);
    setError('');

    try {
      // Submit to topic-specific endpoint
      const res = await axios.post(
        'http://localhost:5001/api/submit-topic-survey',
        {
          topic,
          answers: Object.values(answers)
        },
        {
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      if (res.data.success) {
        localStorage.setItem('subtopics', JSON.stringify(res.data.subtopics));
        navigate('/subtopics');
      } else {
        throw new Error(res.data.message || 'Submission failed');
      }
    } catch (err) {
      setError(err.response?.data?.message || err.message || 'Submission error');
      console.error('Survey submission error:', err);
    } finally {
      setSubmitting(false);
    }
  };

  const handleAnswerChange = (questionIndex, value) => {
    setAnswers(prev => ({
      ...prev,
      [questionIndex]: value
    }));
  };

  if (loading) return <div className="loading">Loading survey questions...</div>;

  if (error || !questions.length) return (
    <div className="error-container">
      <h3>Error loading survey</h3>
      <p>{error || 'No questions available'}</p>
      <button onClick={() => navigate('/home')}>Return to Home</button>
    </div>
  );

  return (
    <div className="survey-container">
      <h2>Topic Survey: {topic}</h2>
      <p className="survey-description">Please answer these questions to help us personalize your learning path</p>

      <div className="questions-list">
        {questions.map((q, i) => (
          <div key={`question-${i}`} className="question-card">
            <h3 className="question-text">
              <span className="question-number">Q{i+1}:</span> {q.question}
            </h3>

            {q.options ? (
              <div className="options-grid">
                {q.options.map((option, j) => (
                  <div
                    key={`option-${j}`}
                    className={`option-card ${answers[i] === option ? 'selected' : ''}`}
                    onClick={() => handleAnswerChange(i, option)}
                  >
                    <div className="option-radio">
                      {answers[i] === option && <div className="option-selected-indicator" />}
                    </div>
                    <div className="option-text">{option}</div>
                  </div>
                ))}
              </div>
            ) : (
              <textarea
                value={answers[i] || ''}
                onChange={(e) => handleAnswerChange(i, e.target.value)}
                placeholder="Type your answer here..."
                rows={3}
                className="answer-textarea"
              />
            )}
          </div>
        ))}
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="survey-actions">
        <button
          onClick={() => navigate('/home')}
          className="secondary-button"
        >
          Back to Topics
        </button>
        <button
          onClick={handleSubmit}
          disabled={submitting || Object.values(answers).some(a => !a)}
          className="primary-button"
        >
          {submitting ? (
            <>
              <span className="spinner"></span> Generating Your Path...
            </>
          ) : (
            'Get My Learning Path'
          )}
        </button>
      </div>
    </div>
  );
}

export default SurveyPage;
