import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/survey.css';

function HomePage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [topic, setTopic] = useState('');
  const [showSurveyForm, setShowSurveyForm] = useState(false);
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSearch = async () => {
    if (!searchTerm.trim()) {
      setError('Please enter a topic');
      return;
    }

    setLoading(true);
  try {
    const res = await axios.post(
      'http://localhost:5001/api/generate-questions', 
      { topic: searchTerm.trim() },
      { 
        withCredentials: true, // This is crucial
        headers: {
          'Content-Type': 'application/json'
        }
      }
    );

      if (res.data.success) {
        setQuestions(res.data.questions);
        setTopic(searchTerm);
        setShowSurveyForm(true);
        setAnswers(res.data.questions.reduce((acc, _, i) => ({ ...acc, [i]: '' }), {}));
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to generate questions');
    } finally {
      setLoading(false);
    }
  };

  const handleSurveySubmit = async () => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5001/api/submit-survey', {
        topic,
        answers: Object.values(answers),
        questions
      });

      if (res.data.success) {
        localStorage.setItem('subtopics', JSON.stringify(res.data.subtopics));
        localStorage.setItem('surveyTopic', topic);
        navigate('/subtopics');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to submit survey');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home-container">
      {!showSurveyForm ? (
        <div className="search-card">
          <h2>What would you like to learn about?</h2>
          <div className="search-bar">
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
              placeholder="Enter a topic (e.g., Python, Calculus)"
            />
            <button 
              onClick={handleSearch}
              disabled={loading}
            >
              {loading ? 'Generating...' : 'Continue'}
            </button>
          </div>
          {error && <p className="error">{error}</p>}
        </div>
      ) : (
        <div className="survey-card">
          <h2>Help us personalize your learning about {topic}</h2>
          <p>Please answer these questions:</p>

          <div className="survey-questions">
            {questions.map((q, i) => (
              <div key={i} className="question">
                <h4>{q.question}</h4>
                <div className="options">
                  {q.options.map((option, j) => (
                    <div 
                      key={j} 
                      className={`option ${answers[i] === option ? 'selected' : ''}`}
                      onClick={() => setAnswers(prev => ({ ...prev, [i]: option }))}
                    >
                      {option}
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>

          <button 
            onClick={handleSurveySubmit}
            disabled={loading || Object.values(answers).some(a => !a)}
            className="submit-btn"
          >
            {loading ? 'Processing...' : 'Get My Learning Path'}
          </button>
        </div>
      )}
    </div>
  );
}

export default HomePage;
