import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate,Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import SignupPage from './pages/SignupPage';
import HomePage from './pages/HomePage';
import SurveyPage from './pages/SurveyPage';
import SubtopicsPage from './pages/SubtopicsPage';
import axios from 'axios'; // Add this import

// Authentication check component
// Modify ProtectedRoute to check session via API
const ProtectedRoute = ({ children }) => {
  const [authChecked, setAuthChecked] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('/api/protected', { withCredentials: true })
      .then(() => {
        setIsAuthenticated(true);
        setAuthChecked(true);
      })
      .catch(() => {
        setIsAuthenticated(false);
        setAuthChecked(true);
        navigate('/login');
      });
  }, [navigate]);

  if (!authChecked) return <div>Loading...</div>;

  return isAuthenticated ? children : null;
};

function App() {
  return (
    <Router>
      <Routes>
        {/* Public routes */}
        <Route path="/login" element={<LoginPage />} />
        <Route path="/signup" element={<SignupPage />} />

        {/* Default route redirects to login or home based on auth status */}
        <Route path="/" element={
          localStorage.getItem('userToken') ? 
            <Navigate to="/home" replace /> : 
            <Navigate to="/login" replace />
        } />

        {/* Protected routes - only accessible when authenticated */}
        <Route path="/home" element={
          <ProtectedRoute>
            <HomePage />
          </ProtectedRoute>
        } />

        <Route path="/survey" element={
          <ProtectedRoute>
            <SurveyPage />
          </ProtectedRoute>
        } />

        <Route path="/subtopics" element={
          <ProtectedRoute>
            <SubtopicsPage />
          </ProtectedRoute>
        } />

        {/* Fallback route for unknown paths */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
