import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/survey.css';

function SubtopicsPage() {
  const navigate = useNavigate();

  // Safely parse localStorage data with proper fallbacks
  const getSubtopics = () => {
    try {
      const stored = localStorage.getItem('subtopics');
      if (!stored) return [];

      const parsed = JSON.parse(stored);
      return Array.isArray(parsed) ? parsed : [];
    } catch (error) {
      console.error('Error parsing subtopics:', error);
      return [];
    }
  };

  const subtopics = getSubtopics();
  const topic = localStorage.getItem('surveyTopic') || 'your selected topic';

  const handleNewSurvey = () => {
    localStorage.removeItem('subtopics');
    localStorage.removeItem('surveyTopic');
    navigate('/home');
  };

  // Early return if no subtopics
  if (subtopics.length === 0) {
    return (
      <div className="subtopics-container">
        <div className="results-card">
          <h2>No Learning Path Generated</h2>
          <p>We couldn't generate a learning path. Please try the survey again.</p>
          <button onClick={handleNewSurvey} className="new-survey-btn">
            Start New Survey
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="subtopics-container">
      <div className="results-card">
        <h2>Your Personalized Learning Path for {topic}</h2>
        <p className="subtitle">Based on your survey answers:</p>

        <div className="subtopics-list">
          {subtopics.map((subtopic, index) => (
            <div key={index} className="subtopic-card">
              <div className="subtopic-number">{index + 1}</div>
              <div className="subtopic-content">
                <h3>{subtopic}</h3>
                <p>Recommended resources will appear here</p>
              </div>
            </div>
          ))}
        </div>

        <div className="actions">
          <button onClick={handleNewSurvey} className="new-survey-btn">
            Start New Survey
          </button>
        </div>
      </div>
    </div>
  );
}

export default SubtopicsPage;
