import React, { useEffect, useState } from "react";
import axios from "axios";

const PersonalityQuestions = ({ onComplete }) => {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch questions from backend API connected to Azure CosmosDB
    axios
      .get("/api/personality-questions")
      .then((response) => {
        setQuestions(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Failed to fetch questions:", error);
        setLoading(false);
      });
  }, []);

  const handleChange = (id, value) => {
    setAnswers((prev) => ({ ...prev, [id]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Send answers to backend to save under user document
    try {
      await axios.post("/api/save-personality", answers);
      onComplete(); // Navigate to home page
    } catch (err) {
      console.error("Error saving answers:", err);
    }
  };

  if (loading) return <p>Loading questions...</p>;

  return (
    <form onSubmit={handleSubmit} className="p-6 max-w-xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Personalize Your Experience</h2>

      {questions.map((q) => (
        <div key={q.id} className="mb-6">
          <label className="block font-medium mb-2">{q.question}</label>

          {q.type === "radio" &&
            q.options.map((option) => (
              <div key={option}>
                <label className="inline-flex items-center">
                  <input
                    type="radio"
                    name={q.id}
                    value={option}
                    checked={answers[q.id] === option}
                    onChange={(e) => handleChange(q.id, e.target.value)}
                    className="mr-2"
                  />
                  {option}
                </label>
              </div>
            ))}

          {q.type === "text" && (
            <input
              type="text"
              name={q.id}
              value={answers[q.id] || ""}
              onChange={(e) => handleChange(q.id, e.target.value)}
              className="border rounded w-full p-2"
            />
          )}
        </div>
      ))}

      <button
        type="submit"
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Continue to Home
      </button>
    </form>
  );
};

export default PersonalityQuestions;
